var niceSelectData={"selector":".nice-select","alignment":"left","fullWidth":"disable","placeholder_text":"","custom_css":"#nice-select {
	min-height: 46px;
}
.nice-select {
 max-width: 350px;
 width: 350px;
 min-height: 46px;
 padding: 9px 12px;
 border: 1px solid #9DA6B6;
 border-radius: 5px;
 line-height: 28px;

 li {
 margin: 0;
 }

 li > div.option-select-item.option-select-item_with-img {
 [ class ^= 'exchange-icon-' ], [ class *= 'exchange-icon-' ] {
 padding-right: 10px;
 font-size: 16px;
 }

 b {
 padding-right: 5px;
 }
 }

 &:after {
 content: '\e935' !important;
 height: 16px !important;
 width: 16px !important;
 color: #9DA6B6 !important;
 font-size: 14px !important;
 font-family: 'buycoin-icon', serif !important;
 display: inline-flex !important;
 align-items: center !important;
 justify-content: center !important;
 border: 1px solid #9DA6B6 !important;
 border-radius: 3px !important;
 transform: rotate(0);
 top: 18px;
 }

 &.open:after {
 content: '\e97c' !important;
 transform: rotate(0);
 }

 & > span.current > div {
 width: calc(100% - 30px);
 padding: 9px 12px;
 color: #19183D;
 display: flex;
 align-items: center;
 text-transform: uppercase;
 text-overflow: ellipsis;
 white-space: nowrap;
 overflow: hidden;


 [ class ^= 'exchange-icon-' ], [ class *= 'exchange-icon-' ] {
 padding-right: 10px;
		font-size: 22px;
 }


 b {
 padding-right: 5px;
 }

 &.selected {
 padding: 0;
 }
 }
 }

 .item-option {
 padding: 3px 10px;
 background: #F3F3F3;
 cursor: pointer;

 &.active {
 background-color: #1c1b30;

 & > *, & b {
 color: #fff;
 }
 }

 &:hover {
 color: #fff;
 background: #A7B3C1;

 b {
 color: #fff;
 }
 }
 }

 ul.list {
 width: 100%;
 max-height: 200px;
 border: 1px solid #E5E5E5;
 box-shadow: 0 4px 15px 4px rgba(120, 141, 172, 0.6) !important;
 overflow-y: scroll !important;
	transition: none !important;


 &::-webkit-scrollbar {
 -webkit-appearance: none;
 width: 8px;
 background-color: #F3F3F3;
 border-radius: 20px;
 }

 &::-webkit-scrollbar-track {
 background: #F3F3F3;
 border-radius: 20px;
 }

 &::-webkit-scrollbar-thumb {
 width: 8px;
 background-color: rgba(0, 0, 0, 0.5);
 border-radius: 12px;
 }
 }

 @media screen and (max-width: 768px) {
 .nice-select {
 max-width: unset;
 width: 100%;
 }
 }"};
